REPLACE INTO `taxa` VALUES (1241,15,'Vicia sativa subsp. macrocarpa','Calice con denti lunghi circa quanto il tubo; corolla 22-30 mm; legume 8-11 X 35-70 mm, generalm. glabro.','2015-12-14 17:25:50','2015-12-15 14:18:11','',1230329);
REPLACE INTO `taxa` VALUES (1240,15,'Vicia sativa subsp. sativa','Calice con denti lunghi quanto il tubo o più; legume 6-11 X 35-70 mm, generalm. pubescente.<br />Rara è la var. cosentini (Guss.) Groves con legumi a sezione circolare e corolla di 18-20 mm: è pianta segetale diffusa in Lig., sul versante occid. della Pen., Otranto, Sic, Sard. e probabilm. corrisponde all\'aspetto originario della subsp.','2015-12-14 17:24:50','2015-12-15 14:14:58','',1230327);
REPLACE INTO `taxa` VALUES (1239,15,'Vicia sativa subsp. cordata','Circa intermedia tra le subsp. (a) e (b) (ibrido fissato?): calice con denti &gt; tubo; corolla 18-20 mm; legume 4.5-6 X 30-50 mm, generalm. glabro.','2015-12-14 17:24:12','2015-12-15 14:17:16','',1271069);
REPLACE INTO `taxa` VALUES (1238,15,'Vicia sativa subsp. segetalis','Calice con tubo di 6-7 mm e denti di 4-5 mm; corolla 15-17 mm; legume 4-5 x 40-45 mm, generalm. glabro.','2015-12-14 17:23:41','2015-12-15 14:19:33','',NULL);
REPLACE INTO `taxa` VALUES (1237,15,'Vicia sativa subsp. angustifolia','Calice con tubo di 4-5 mm e denti di 2-3 mm; corolla 14-16 mm; legume 3-4 X 30-40 mm, generalm. glabro.','2015-12-14 17:23:14','2015-12-15 14:16:12','',19864505);
REPLACE INTO `taxa` VALUES (1236,15,'Vicia amphicarpa','Pianta portante alla base f. stoloniformi sotterranei, clorotici e con sole squame ovate; all\'apice di questi si formano fi. cleistogami con corolle rudimentali che formano piccoli legumi a 1-2 semi. Per il resto abbastanza simile a 1712 (b): calice con denti &lt; tubo; corolla 20-25 mm; legume 4-6 X 25-35 mm.<br /><br />Steno-Medit.<br />Pen. Salentina, Basii, Cal, Sic. e Cors.: R o poco osservata.','2015-12-14 17:20:03','2015-12-15 14:22:56','05d406adab63a37de1ad5f0f5082f36b',NULL);
REPLACE INTO `taxa` VALUES (1235,15,'Vicia sativa','Si compone di 4 subsp. collegate da intermedi ed ibridabili fra loro:<br /><br />Medit. - Turan. divenuta Subcosm.<br />In tutto il terr.','2015-12-14 17:18:52','2015-12-15 14:12:39','fd93b96763310bc3ef902ce2dee046cb',703227);
REPLACE INTO `taxa` VALUES (1234,15,'Vicia pimpinelloides','Segm. maggiori delle fg. più sviluppate, su ciascun lato con 2-3 denti profondi fino ad ¼-⅓ della lamina; calice con denti lunghi circa quanto il tubo; corolla 10-11 mm; legume 6-7 X 35-42 mm, glabro.','2015-12-14 17:17:57','2015-12-15 14:26:35','32d8178359703098b39ebce715ffd0fd',NULL);
