REPLACE INTO `taxa` VALUES (1000,12,'Tetragonolobus','Simile a <em>Gen. Lotus</em>, però la coppia dei segm. basali è costituita da vere stipole, quindi fg. 3 fogliate; legume generalm. a sez. 4angolare.','2015-10-27 12:37:53','2016-02-29 14:15:15','',NULL);
REPLACE INTO `taxa` VALUES (999,12,'Lotus','Fg. a 5 segni., i due basali con l\'aspetto di stipole (le vere stipole sono ridottissime); la nervatura che collega ì\'un paio di segm. all\'altro ha l\'aspetto di un picciuolo: qui viene indicata come rachide.','2015-10-27 12:37:19','2016-02-27 07:42:10','c8aee5f9705134e1ee5d8d2513a61f4f',27791);
