REPLACE INTO `taxa` VALUES (996,15,'Vigna unguiculata','Simile a {t1675}, ma fg. con segm. più lungam. acuminati e stipole maggiori; corolla gialla o rossa, 2 cm o più; legume sottile (0.5 X 10-30 cm)<br /><br />Paleotrop.<br />In tutto il terr. (origin. forse dell\'Africa Tropic, nel Medit. diffuso già durante l\'Impero Romano).<br /><br />Vigna D. († 1647), botanico a Pisa nella prima metà del sec. XVII.','2015-10-27 12:32:08','2015-11-23 16:06:58','0c911baf5b8637adecb331ea580d9407',655178);
