REPLACE INTO `taxa` VALUES (840,15,'Alchemilla monticola','Pianta verde-bluastra (nel secco verde-bruna). F. legnoso sotterraneo con squame ferruginee; pelosità patente. Fg. basali con picciuolo di 1-2 dm, densam. tomentoso; lamina tomentosa sulle due facce, subrotonda con insenatura basale stretta; lobi 9-11 profondi ⅓-⅖, semicircolari, con 7-9 denti ± eguali, acuti e conniventi; incisioni brevi. F. fioriferi eretti, gracili, tomentosi, poco (±⅓) più alti delle fg.; inflor. con rami e peduncoli glabri; glomeruli densi; fi. 3 mm.<br /><br />Alpi, App. Sett. e Centr.: C.<br /><br />In tutta Eur. e probabilm. altrove nell\'Emisf. Boreale.','2015-10-06 16:43:54','2015-10-08 12:35:04','e47fa334065322ee3357c91f01ad35c6',791888);
REPLACE INTO `taxa` VALUES (839,15,'Alchemilla filicaulis','Alpi Or., dalle Dolom. alle Grigne: C; A. Maritt','2015-10-06 16:43:25','2015-10-08 12:55:17','9fc74ccc0ea4a3661b4fc869c0fa36a7',414356);
REPLACE INTO `taxa` VALUES (838,15,'Alchemilla acutiloba','Pusteria, Dolom., Grigne, C. Tic: R.','2015-10-06 16:42:47','2015-10-08 12:44:52','7ff2fa29db2dc84951d5a84324c8edb4',637557);
REPLACE INTO `taxa` VALUES (837,15,'Alchemilla xanthochlora','Simile a <em>Alchemilla monticola</em>, ma pianta verde-giallastra; pelosità patente o eretto-patente; lamina glabra di sopra, tomentosa di sotto, ± reniforme con insenatura basale larga; lobi profondi ¼-⅓, allungati; denti larghi, diritti; inflor. glabra o con peli sparsi; fi. 2.5-3.0 mm.<br /><br />Alpi, dalle Giulie alle Mariti.: C; App. Tosco-Emil, Centr. al M. Tre Confini, Pollino e Sila.<br /><br />Centroeurop.<br /><br />1503/c. A. marsica Buser - 3375266 -Abr. a Villavallelonga.','2015-10-06 16:42:15','2015-10-09 11:47:54','2f299003f383266bfe94209d6c259e02',633495);
REPLACE INTO `taxa` VALUES (836,15,'Alchemilla gracilis','Pusteria, Dolom., Bormiese, Grigne, C. Tic. e segnalata in molte altre località nelle Alpi.','2015-10-06 16:41:15','2015-10-08 12:46:22','31c8a398499c060df23f4e29c96d99ef',414388);
