REPLACE INTO `taxa` VALUES (717,15,'Rubus furvus','Segnalato nella Lig. Occid. e Teram., ma da eliminare.','2015-08-16 09:45:41','2015-08-16 09:45:41','',411782);
REPLACE INTO `taxa` VALUES (716,15,'Rubus schleicheri','Segnalato nel Goriz., Friuli e Teram.: la presenza di questa specie in Italia non è impossibile, ma andrebbe verificata.','2015-08-16 09:43:51','2015-08-16 09:43:51','2012cdf4fdf8437117f6d5c4a4848b01',16897650);
