REPLACE INTO `taxa` VALUES (714,15,'Rubus koehleri','Segnalato nel Goriz., Montello,Grigne, C Tic, Lig. e Teram., ma per errore; da noi tuttavia esistono specie poco differenti.','2015-08-16 09:32:24','2015-08-16 09:32:24','992b05acdbdd3236b59f1d6b5ba08dd3',NULL);
REPLACE INTO `taxa` VALUES (713,15,'Rubus pilocarpus','Grigne, Alpi Cozie e Mariti.: segnalato anche nel C. Tic.','2015-08-16 09:30:40','2015-08-16 09:32:50','',631803);
