REPLACE INTO `taxa` VALUES (710,15,'Rubus lejeunei','Segnalato sulle Alpi Cozie, ma per errore','2015-08-16 09:25:42','2015-08-16 09:25:42','39040a4f6fb235cf52b32bb14c62b33d',624532);
REPLACE INTO `taxa` VALUES (709,15,'Rubus caflischii','Segnalato nel Goriz.: da verificare.','2015-08-15 13:24:52','2015-08-15 13:30:38','d72a9de4a64c1887216f1755fde3d19d',625019);
REPLACE INTO `taxa` VALUES (708,15,'Rubus glaucellus','Segnalato in Tose, a Boscolungo e nel Teram., ma per errore.','2015-08-15 13:24:18','2015-08-15 13:29:27','',16901989);
