REPLACE INTO `taxa` VALUES (705,15,'Rubus pallidus','Segnalato nel Friuli Or., Carnia, Cadore, Trent., Teram. e Garg.: scarsam. verosimile e da verificare.','2015-08-13 09:29:23','2015-08-13 09:29:23','7f126b3e914c81b9ff95454cc2325eeb',244732);
REPLACE INTO `taxa` VALUES (704,15,'Rubus bregutiensis','Presso il confine or., ma in terr. jugoslavo.','2015-08-13 09:27:04','2015-08-13 09:27:04','a12901ad106bef6bf5b285b337bfe4a1',629887);
REPLACE INTO `taxa` VALUES (703,15,'Rubus menkei','Cadore, Teramano.','2015-08-13 09:24:55','2015-08-13 09:24:55','48f27a11ac1d632563b3120fc0d8b0e7',244191);
REPLACE INTO `taxa` VALUES (702,15,'Rubus corymbosus','Le segnalazioni dalle Grigne e Teramano sotto questo nome vanno riferite a <em>Rubus flexuosus</em> o specie affine.','2015-08-13 09:22:14','2015-08-13 09:22:14','07422b79d693d7398cde54d33d20f7eb',230326);
REPLACE INTO `taxa` VALUES (701,15,'Rubus flexuosus','Segnalato in Carnia, Trent, in V. Pine e V. Aosta: scarsam. verosimile e da verificare.','2015-08-13 09:19:46','2015-08-13 09:19:46','ef03914e5892421a76f16767d43f1dc7',632994);
REPLACE INTO `taxa` VALUES (700,15,'Rubus foliosus','Segnalato nel Tirolo Merid. e Grigne: scarsam. verosimile e da verificare.','2015-08-13 09:12:56','2015-08-13 09:12:56','8bba218d3489d7f0058e19e37a52575f',623967);
REPLACE INTO `taxa` VALUES (699,15,'Rubus fuscus','Segnalato in Basil, e Sard, ma per errore.','2015-08-13 09:09:46','2015-08-13 09:09:46','0461d032194f4681010db9250e4247ab',245501);
REPLACE INTO `taxa` VALUES (698,15,'Rubus apiculatus','Segnalato nel Teramano, ma per errore.','2015-08-13 09:06:38','2015-08-13 09:06:38','',623043);
