REPLACE INTO `taxa` VALUES (696,15,'Rubus adscitus','Pr. Como, nelle A. Mariti., ma da verificare.','2015-08-12 15:44:01','2015-08-12 15:44:01','f34c108f27a75c0d777efbf9738dddcd',300830);
