REPLACE INTO `taxa` VALUES (694,15,'Rubus teretiusculus','Goriziano, Friuli Or., Tose, a Vallombrosa, certo per errore e da eliminare.','2015-08-12 15:38:26','2015-08-12 15:38:26','dedfede3b3bd0baeca10fd687fb24659',631444);
