REPLACE INTO `taxa` VALUES (248,15,'Scutellaria minor','Caratteri di Scutellaria galericulata, ma fg. lanceolate a ovate 3-8 X 6-20 mm, intere o con 1-2 dentelli basali; corolla rosea (6-7 mm), densam. pubescente, con tubo quasi diritto.','2014-10-30 12:10:09','2015-08-03 12:21:04','2d1b2780e879c73af67a198beb7691f5',0);
REPLACE INTO `taxa` VALUES (247,15,'Scutellaria hastifolia','Simile a Scutellaria galericulata, ma fg. più decisam. astate alla base, le inf. con 2-3 dentelli basali, le sup. generalm. a margine intero; calice con peli ghiandolari; corolla 20-22 mm, nei verticilli sup. > fg. ascellante.','2014-10-30 12:09:21','2015-08-03 12:20:25','bb19fa7fad7403395b0b6e4be1aee5b5',5367128);
REPLACE INTO `taxa` VALUES (246,15,'Scutellaria galericulata','F. eretti, 4angolari, generalm. glabri, semplici o con rami arcuati, stoloni basali striscianti. Fg. con picciuolo di 1-4 mm e lamina lanceolata 8-13 X 32-40 mm, dentellata; base arrotondata, troncata o debolm. astata. Fi. a (1)2 all\'asc. di fg. normali; calice generalm. violaceo 3 mm; corolla 10-15 mm, con tubo curvato.','2014-10-30 12:08:28','2015-08-03 12:19:58','d6b50fad82caa96d49ebb82a78e71157',578026);
REPLACE INTO `taxa` VALUES (245,15,'Scutellaria albida','Simile a <strong>Scutellaria altissima</strong>, ma fg. con picciuolo di 1 cm e lamina di 2 X 3-3.5 cm, con 8-10 denti per lato, eccezionalm. anche picciuolo di 2 cm e lamina di 3 X 5; brattee vistose, lanceolate, intere (le med. di 5-7 X 12-15 mm, le inf. maggiori); calice di 3 mm; corolla chiara 13-16 mm.','2014-10-30 12:07:07','2015-08-03 12:19:38','ca3d6f9519c2d34afc5937f2e9b36ac8',5367666);
REPLACE INTO `taxa` VALUES (244,15,'Scutellaria rubiconda','Simile a Scutellaria Altissima, ma fg. con lamina triangolare-ovata 2-3 X 3-4 cm; denti 6-7 per lato, profondi; brattee (3-)7-8 (-10) mm; calice (3-5 mm) e tubo corollino densam. tomentosi; corolla purpurea 12-14 mm.','2014-10-30 12:06:10','2014-11-04 10:31:49','',NULL);
REPLACE INTO `taxa` VALUES (243,15,'Scutellaria altissima','F. eretti, 4angolari, con peli di 0.2-0.3 mm. Fg.con picciuolo di 1-2 cm e lamina tenue, scura nel secco, 3angolare 3.5-5 X 7-10 cm; denti 16-18 per lato, profondi. Racemi allungati, con fi. appaiati, distanziati; brattee ellittico-acuminate 3-5(8) mm; calice subglabro 3-4 mm; corolla violacea 10-14 mm.','2014-10-30 12:05:22','2015-08-03 12:18:01','804d50d855367af4d76e85ab55f18605',481626);
REPLACE INTO `taxa` VALUES (242,15,'Scutellaria alpina','F. prostrato-ascendenti, legnosi alla base, in alto con peli patenti di 1.0-1.4 mm. Fg. inf. con picciuolo di 3-8 mm e lamina lanceolata di 7-10 X 15-20 mm, acutam. dentata; fg. sup. ridotte. Inflor. densa, 4angolare (2-4 cm); brattee lanceolato-acuminate 4-6 x 8-13 mm, intere; calice 5-6 mm; corolla purpurea o violacea 23-25 mm.</p>\r\nAlpi Occ. dalla Valsesia al mare; App. March. (M. Pescolla), Terminillo, App. Abr. (Gr. Sasso, Majélla, Velino e Pizzodetta) e Pollino: R; segnalata anche nel Bormiese.ed Alpi Ven., ma da ricercare.','2014-10-30 12:04:32','2015-08-03 12:17:19','2cc1fb40d065b85d3c34cb26d9513555',5366931);
REPLACE INTO `taxa` VALUES (241,15,'Scutellaria columnae','F. eretti, pubescenti ed in alto con peli ghiandolari di 0.4-0.5 mm. Fg. inf. con picciuolo di 4-6 cm e lamina cuoriforme; denti 9-13 per lato, arrotondati. Fi. appaiati in lunghe inflor. unilaterali; brattee intere; calice 4 mm; corolla purpurea 20-24 mm, con tubo curvato.','2014-10-30 12:03:45','2015-08-03 12:16:33','a48ae80226336f286f06c9f5e69bd3aa',5367029);
