REPLACE INTO `taxa` VALUES (200,15,'Ocimum basilicum','Pianta glabra, profumata. F. eretti, ramosi. Fg. con picciuolo di 5-15 mm e lamina lanceolata a ovale (2-3 X 3-4 cm), intere. Verticillastri 4-6flori all\'asc. di fg. ridotte, le sup. spesso arrossate; calice 5-6 mm con labbro sup. fogliaceo; corolla bianca o ± rosea 10-12 mm; stami 4, giacenti sul labbro inf. e sporgenti oltre questo.','2014-10-15 16:08:39','2015-08-03 14:07:34','87a4d6b27bcfb3f8bda26f02883da888',579364);
