REPLACE INTO `taxa` VALUES (189,15,'Moluccella spinosa','F. eretti, glabri. Fg. con picciuolo di 1 cm e lamina cuoriforme (2-6 cm), grossam. dentata sul bordo. Verticillastri 6-10 fiori con bratteole lesiniformi, spinose; calice con tubo di 10 mm e denti spinosi di 5-12 mm, formanti 2 labbra; corolla bianco-rosea, 25-35 mm.','2014-10-15 15:57:09','2015-08-03 12:21:57','d0be2839252dcb729c51f32472023c30',5371145);
