REPLACE INTO `taxa` VALUES (2238,12,'Adenostyles','Piante perenni con fg. alterne, intere; capolini oo con pochi fi.; squame in 1(2) serie; ricettacolo nudo; fi. ligulati assenti; fi. tubulosi ermafroditi; pappo di peli; antere arrotondate alla base; stili cilindrici. - Bibl.: Braun J., Vierteljahrschr. Naturf. Ges. Ztirich 58: 92-96 (1913); Vierhapper F., Oesterr. Boi Z. 72: 150-164 (1923).','2016-06-29 09:41:41','2016-06-30 12:02:41','288f67859645fd3f15351c3cedc8ff00',6262228,0);
