REPLACE INTO `taxa` VALUES (2228,15,'Phyllostachys bambusoides','Simile a {t5294/VI}, ma internodi in sez. a 3-4 angoli; fg. larghe 12-15 mm, variegate in bruno o violetto; ligula 2 mm, con peli di 5-7 mm.<br /><br />Giaoppone.','2016-06-28 17:11:27','2016-06-28 17:11:29','c66546a26d9b20a9a1600a93af9a3ea1',1115497,0);
REPLACE INTO `taxa` VALUES (2227,15,'Phyllostachys mitis','Culmi elevati, diritti, rigidi, alla base ingrossati (diam. fino a 20 cm), di colore verde-glauco, in seguito giallastri; fg. ruvide, con guaine screziate di porporino e lamina lineare (1-1.3 X 10-15 cm); ligula breve (1 mm), screziata di porpora.<br />Giappone.','2016-06-28 17:09:08','2016-06-28 17:09:09','c811ae3d709646296ea91d75a1270807',NULL,0);
REPLACE INTO `taxa` VALUES (2226,15,'Phyllostachys nigra','Culmi robusti, eretti; fg. con guaine violacee e lamina lanceolato-lineare (1 X 6-12 cm), cigliata, almeno sul bordo; ligula di 2 mm, con lunghi peli; sp.tte 2flore con rudimento di fi. sterile; glume e lemmi pelosi; talvolta fiorisce anche da noi (con un ciclo circa trentennale).<br /><br />E-Asiat.','2016-06-28 17:04:32','2016-06-28 17:12:19','a1f1ec1628b933995d37a1293c7a10e1',1115500,0);
REPLACE INTO `taxa` VALUES (2225,15,'Bambusa angustifolia','Simile a {5294/II}, ma più gracile; fg. variegate, dentellate, di 0.5 X 5-10 cm.<br /><br />Giappone.','2016-06-28 16:57:30','2016-06-28 16:57:32','9a50e9f0135dcb3aa96bad1cdfbe69ab',NULL,0);
REPLACE INTO `taxa` VALUES (2224,15,'Bambusa pygmaea','Culmi  robusti, ma brevi, generalm. violetti; fg. con guaina glabra e lamina lanceolata (1-1.5 X 7-10 cm), ruvida sul margine e generalm. pubescente nella metà inf.; ligula ridotta ad un anello di peli.<br /><br />Giappone.','2016-06-28 16:55:12','2016-06-28 16:55:13','ddb3597a75b3f05f05f3287e47859bbd',NULL,0);
