REPLACE INTO `taxa` VALUES (1698,12,'Pennisetum','','2016-03-09 15:51:05','2016-06-23 17:45:36','274f3bd9616f20defbdb789d56e2314b',108192,0);
REPLACE INTO `taxa` VALUES (1697,12,'Setaria','Bibl:.Hubbard F T Amer-J- Bot 2: 169-198 (1915); Rominger J. M., IllinoisBiol. Monogr. 29, 132 pagg. (1962). 6','2016-03-09 15:50:43','2016-06-23 09:29:05','5f173c968be01de7c603c360316c85b2',108098,0);
REPLACE INTO `taxa` VALUES (1696,15,'Imperata cylindrica','Rz. obliquo allungato; culmi eretti, cilindrici, lisci, alla base circondati da guaine squami-formi biancastre. Fg. rigide, scabre, convolute, le basali con lamina larga fino a 7 mm, le cauline più strette, spesso arrossate e con guaina un po\' rigonfia; ligula di peli (2-3 mm). Pannocchia spiciforme cilindrica (1 X 6-10 cm), bianco-cotonosa; sp.tte 6 mm, appaiate (una sessile e l\'altra peduncolata), entrambe con 1 fi. ermafr. ed un fi. sterile ridotto al lemma; glume villose, generalm. violacee; antere rosso-aranciate 3 mm.<br /><br />Termocosmop.<br />Lig., Pen. (verso N fino a Porto Civitanova e Massa), Sic., Sard, Cors. ed Is. minori: R. Da noi solo la var. europaea (Pers.) Anderss., S-Medit.<br /><br />Imperato F. (1550-1625), botanico napoletano.','2016-03-09 15:50:24','2016-06-26 09:00:21','94026ba53907bc332da3ea521c9ceef9',1114980,0);
REPLACE INTO `taxa` VALUES (1695,15,'Lagurus ovatus','Culmi fascicolati ascendenti, lisci, glabri, avvolti dalle guaine solo nella metà inf. Fg. mollem. pubescenti con lamina larga 2-4(10) mm e ligula breve (1-2 mm); fg. sup. con guaina un po\' rigonfia. Pannocchia spiciforme generalm. ovata, bianco-lanosa, sormontata dalle reste; glume subeguali (1 X 10 mm), villose; lemma 3 mm, con resta dorsale di 2 cm circa, alla base della quale sono due subule di 2-6 mm.<br /><br />Euri-Medit.<br />Lig., Pen. (lungo le coste), Sic, Sard., Cors. ed in tutte le Is. minori: C; anche sul litorale pr. Venezia; all\'interno diffuso nella Pen., raro ed incostante (Padov., Mantov.) nella Pad.','2016-03-09 15:50:00','2016-06-04 08:47:16','315f964a3ad5e22dffda7abe4b5bfaf0',1114993,0);
REPLACE INTO `taxa` VALUES (1694,12,'Eleusine','','2016-03-09 15:45:41','2016-06-17 12:49:46','54bcaf7580990a5a425fe036bc5d0449',108142,0);
REPLACE INTO `taxa` VALUES (1693,15,'Dactyloctaenium aegyptium','Culmi fascicolati, prostrato-radicanti o ginocchiati. Fg. con lamina larga 3-6 mm, cigliata alla base e sul bordo; ligula troncata o sfrangiata, 1 mm. In-flor. costituita da (2)4-5(6) spighe digitate, patenti, lineari (3-6 X 30-60 mm), con rachide terminata da un rozzo mucrone; sp.tte unilaterali, su 2 serie, con 2(4) fi. fertili ed 1 abortivo ridottissimo; glume 2 mm, la sup. con resta di 1-2.5 mm; lemma 2.5-3.5 mm, acuminato.<br /><br />Paleotrop. e subtrop.<br />Lazio, Camp., Cai. e Sic, avv. in espansione, segnalata anche pr. Torino.','2016-03-09 15:45:11','2016-04-27 11:34:50','',NULL,0);
REPLACE INTO `taxa` VALUES (1692,15,'Cynodon dactylon','Rz. tenace, strisciante e radicante ai nodi, con fg. distiche; culmi ascendenti, avvolti dalle guaine fino all\'inflor. Fg. con lamina larga 3-3.5 mm, quelle dei getti sterili brevi, lanceolate, le altre lunghe 3-5 cm; lamina canalicolata, rigida, irta di peli patenti; ligula sostituita da peli di 1.5 mm. Spighe digitate a 3-5, sottili, generalm. violette; sp.tte 1flore (con il rudimento di un fi. sup. abortivo); glume di 0.7 e 2.6 mm rispettivam.; lemma 2.3 mm.<br /><br />Termo-Cosmop.<br />In tutto il terr.: CC.','2016-03-09 15:44:44','2016-06-18 15:51:32','6581586a68ab2331b5c681de84a69973',1114871,0);
