REPLACE INTO `taxa` VALUES (1655,15,'Chaetopogon fasciculatus','Culmi eretti, gracili, fascicolati. Fg. glabre, filiformi, con ligula di 2 mm. Pannocchia spiciforme povera, contratta; sp.tte lineari, uniflo-re, riunite in fascetti di 2-3 su peduncoli brevi; glume 3 mm, l\'inf. con resta apicale di 3-4 mm, la sup. acuta, ma non aristata; lemma 2.5 mm, glabro.<br /><br />Osservata anticam. sui C. Eug., pr. Livorno ed a Biccari (Foggia), ma non ritrovata di recente.<br /><br />SW-Europ.','2016-03-09 15:06:15','2016-06-03 07:29:26','62014707263451b1ac262fb2f8ac1ebb',5798271,0);
