REPLACE INTO `taxa` VALUES (989,15,'Adenocarpus complicatus','Rami a corteccia grigio-chiara, alla fine subspinescenti. Fg. con picciuolo di 3-6 mm e 3 segm. oblanceolati (2-4 X 7-15 mm), generalm. revoluti, sparsam. pubescenti. Racemi allungati (5-10 cm) terminali, afilli; picciuoli fior. 3-6 mm; calice 3-4 mm; corolla giallo-sulfurea 9-12 mm, con vessillo &lt; carena; legume appiattito (4-5 X 20-30 mm), ondulato, con tubercoli ghiandolari.<br /><br />Steno-Medit.','2015-10-27 12:27:36','2015-11-13 15:42:44','e210cc6dda16c52f3ab48b2869af57bd',703683,0);
