REPLACE INTO `taxa` VALUES (869,15,'Alchemilla hoppeana','Segnalata su tutte le Alpi e sull\'App. dall\'Emilia al Pollino, ma probabilm. per confus. con <em>Alchemilla pallens</em>; pare sia da escludere dalla nostra flora.','2015-10-07 11:53:06','2015-10-07 12:06:12','116238c2babc61eb8d4f5d3e833e20f0',414412,0);
