REPLACE INTO `taxa` VALUES (862,15,'Alchemilla transiens','Alpi Merid. sul M. Baldo, gr. della Cima d\'Asta e Giudicane; App. Emil. sulM. Ragolo, Ventasso, Cintone, Corno alle Scale; Cors.; da ricercare nell\'App. Centr. e Merid.<br /><br />1476/b. A. lucida Buser - 3375005 -Alpi frane, sul Lautaret e pr. Tenda.','2015-10-07 11:23:52','2015-10-07 11:30:18','bd35910933e4ed66e3aa07e6f0a71cc1',11165946,0);
REPLACE INTO `taxa` VALUES (861,15,'Alchemilla alpina','Alpi, dalla V. Pusteria, Bolzano e V. Genova alla Lomb., Piem. ed A. Mariti.; segnalata ancora sull\'App. e ril. minori fino al Pollino, ma probabilm. confusa con 1475-1476.<br /><br />1477/b. A. debilicaulis Buser - È probabilm. una var. a inflor. più lassa e fg. in prevalenza con 5 segni., nota per diverse zone delle Alpi Occìd. e Cors.<br />1477/c. A. alp. var. burnatiana Buser - È un tipo lussureggiante con habitat rupestre. - Cors. sul Paglia Orba.','2015-10-07 11:23:15','2015-10-07 11:32:52','a63d3ff0e7605835e32a429607ffbd71',628526,0);
