REPLACE INTO `taxa` VALUES (837,15,'Alchemilla xanthochlora','Simile a <em>Alchemilla monticola</em>, ma pianta verde-giallastra; pelosità patente o eretto-patente; lamina glabra di sopra, tomentosa di sotto, ± reniforme con insenatura basale larga; lobi profondi ¼-⅓, allungati; denti larghi, diritti; inflor. glabra o con peli sparsi; fi. 2.5-3.0 mm.<br /><br />Alpi, dalle Giulie alle Mariti.: C; App. Tosco-Emil, Centr. al M. Tre Confini, Pollino e Sila.<br /><br />Centroeurop.<br /><br />1503/c. A. marsica Buser - 3375266 -Abr. a Villavallelonga.','2015-10-06 16:42:15','2015-10-09 11:47:54','2f299003f383266bfe94209d6c259e02',633495,0);
