REPLACE INTO `taxa` VALUES (616,15,'Aremonia agrimonoides','Rz. grosso, legnoso; scapi erbacei con alcune fg. basali in rosette e pochi fi. Fg. imparipennate (10-15 cm) con 3 segm. terminali subeguali fra loro, ovoidi (2x3-4 cm) e 2-4 paia di S€gm. inf. assai più piccoli. Cime corimbose lasse con 2-3 fi. (diam. 5-10 mm); 5 petali gialli, spatolati lunghi 5-7 mm; stami 5-10; fr. globoso, 1-2-spermo.','2015-07-26 16:46:31','2015-09-14 16:35:08','9c692bf3e54e9c56ed5cc78a9c8b20c0',245538,0);
