REPLACE INTO `taxa` VALUES (440,15,'Mentha X piperita (I e II)','(I) subhyb. piperita - 3249-x 3253 (b). -Fg. ovali, spesso acute; inflor. in spiga ± larga almeno sul fusto principale. -Forme diverse coltivate e raram. inselvatichite nelllt. Sett. e Centr.<br />(II) subhyb. citrato Ehrh. - 3249 X 3249fb. - Fg. larghe e ottuse; inflor. ridotta quasi ad un glomerulo ± sferico su tutti i f. - Coltivata e raram. inselvatichita.','2015-04-30 17:04:23','2015-04-30 17:13:59','',NULL,0);
REPLACE INTO `taxa` VALUES (439,15,'Mentha acquatica subsp. ortmanniana','Complesso di piante ottenute per ibridazione tra 3249 e 3253 ed esistenti solo allo stato coltivato','2015-04-30 17:02:54','2015-04-30 17:12:57','',NULL,0);
