REPLACE INTO `taxa` VALUES (192,15,'Prasium majus','F. legnosi con corteccia grigio-giallastra a frattura longitudinale, i più giovani acutam. 4angolari, generalm. arrossati e glabri oppure con peli spinulosi di 0.5-0.8 mm. Fg. con picciuolo lungo 1/2-3/4 della lamina, questa lanceolata (7-15 x 15-40 mm), seghettata sul bordo. Verticillastri 2flori ravvicinati; brattee sessili lanceolate 6x15 mm, intere; calice con tubo di 5-6 mm e denti di 6-7 mm; corolla bianca o ± violacea, 17-20 mm.','2014-10-15 15:58:39','2015-08-03 12:24:11','d662ad1872a84da32c6287d0051bba07',2917683,0);
