REPLACE INTO `taxa` VALUES (189,15,'Moluccella spinosa','F. eretti, glabri. Fg. con picciuolo di 1 cm e lamina cuoriforme (2-6 cm), grossam. dentata sul bordo. Verticillastri 6-10 fiori con bratteole lesiniformi, spinose; calice con tubo di 10 mm e denti spinosi di 5-12 mm, formanti 2 labbra; corolla bianco-rosea, 25-35 mm.','2014-10-15 15:57:09','2015-08-03 12:21:57','d0be2839252dcb729c51f32472023c30',5371145,0);
REPLACE INTO `taxa` VALUES (188,12,'Scutellaria','Attenzione! La corolla all\'antesi si allunga notevolmente: le misure si riferiscono pertanto a questa fase. - Bibl.: Rechinger K. H., Bot. Archiv. 43: 1-70 (1941); Bothmer R von Bot Notiser 122: 38-56 (1969).','2014-10-15 15:56:43','2015-08-03 12:15:58','e5fd679da7de310f5a634f5da8bc8387',38213,0);
