REPLACE INTO `taxa` VALUES (185,15,'Perilla frutescens','F. eretto, grosso, solcato. Fg. con picciuolo allungato e lamina ovata (3-6 X 4-10 cm), grossam. dentate. Inflor. 3-10 cm; verticillastri 2flori all\'asc. di fg. ridotte; calice 3-4 mm, allungato alla frutt; corolla biancastra 3-4 mm.','2014-10-15 15:54:39','2015-08-03 11:52:22','1bd3b270feceaa1ecc5206a055284c87',579366,0);
REPLACE INTO `taxa` VALUES (184,15,'Ziziphora capitata','F. eretto, semplice o con rami corimbosi, ispido. Fg. lineari-lanceolate a lanceolate (2-6 X 5-30 mm), acute, intere, generalm. violacee di sotto e con ghiandole puntiformi. Inflor. breve, capituliforme; brattee ovate o rombiche, acuminate; calice 13nervio con tubo incurvato di 5-9 mm e denti 1-2 mm; corolla rosea 10-15 mm.</p>\r\nAvventizia dalla fine del sec. XVIII in Tosc, ora diffusa in tutta l\'It. Centr. e nella Romagna fino al Bolognese: R.','2014-10-15 15:53:40','2015-08-03 11:35:15','0cb8a187e9fb747a3b9e79e93aae0049',5378143,0);
